import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import { Toast } from "../utils/sweetalert";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const { authenticatedUser } = useAuth();
  const axiosInstance = useAxios();
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [parts, setParts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  /* ------------------ REPORT -------------------- */
  // Fetch all reports
  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("reports/");
      setReports(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      const response = await axiosInstance.post("reports/", reportData);
      setReports((prevReports) => [...prevReports, response.data]);
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report created successfully!",
      });
      return response.data;
    } catch (err) {
      handleError(err);
      return null;
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedFields) => {
    try {
      const response = await axiosInstance.patch(
        `reports/${reportId}/`,
        updatedFields
      );
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report updated successfully!",
      });
      return response.data;
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    // Debug
    console.log("Deleting report data:", reportId);

    try {
      await axiosInstance.delete(`reports/${reportId}/`);
      setReports((prevReports) =>
        prevReports.filter((report) => report.id !== reportId)
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Report deleted successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  /* ------------------ TASK -------------------- */
  // Fetch tasks linked to a report
  const fetchTasksByReport = async (reportId) => {
    try {
      const response = await axiosInstance.get(`reports/${reportId}/tasks/`);
      setTasks(response.data);
      return response.data;
    } catch (err) {
      handleError(err);
    }
  };

  // Create a task
  const createTask = async (taskData) => {
    try {
      const response = await axiosInstance.post(`tasks/`, taskData);
    } catch (err) {
      handleError(err);
    }
  };

  // Update a task
  const updateTask = async (taskId, updatedFields) => {
    try {
      const response = await axiosInstance.patch(
        `tasks/${taskId}/`,
        updatedFields
      );
      setTasks((prevTasks) =>
        prevTasks.map((task) => (task.id === taskId ? response.data : task))
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a task
  const deleteTask = async (taskId) => {
    try {
      await axiosInstance.delete(`tasks/${taskId}/`);
      setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
    } catch (err) {
      handleError(err);
    }
  };

  /* ------------------ PART -------------------- */
  // Fetch parts linked to a report
  const fetchPartsByReport = async (reportId) => {
    try {
      const response = await axiosInstance.get(`reports/${reportId}/parts/`);
      setParts(response.data);
      return response.data;
    } catch (err) {
      handleError(err);
    }
  };

  // Create a part
  const createPart = async (partData) => {
    try {
      const response = await axiosInstance.post(`parts/`, partData);
    } catch (err) {
      handleError(err);
    }
  };

  // Update a part
  const updatePart = async (partId, updatedFields) => {
    try {
      const response = await axiosInstance.patch(
        `parts/${partId}/`,
        updatedFields
      );
      setParts((prevParts) =>
        prevParts.map((part) => (part.id === partId ? response.data : part))
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a part
  const deletePart = async (partId) => {
    try {
      await axiosInstance.delete(`parts/${partId}/`);
      setParts((prevParts) => prevParts.filter((part) => part.id !== partId));
    } catch (err) {
      handleError(err);
    }
  };

  // Load reports when user logs in or changes
  useEffect(() => {
    if (authenticatedUser && localStorage.getItem("token")) fetchReports();
    else {
      setReports([]);
      setTasks([]);
      setParts([]);
    }
  }, [authenticatedUser]);

  // Set tasks and parts data when selectedReport changes
  useEffect(() => {
    if (selectedReport && selectedReport.id) {
      fetchTasksByReport(selectedReport.id);
      fetchPartsByReport(selectedReport.id);
    } else {
      setTasks([]);
      setParts([]);
    }
  }, [selectedReport]);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        tasks,
        setTasks,
        createTask,
        fetchTasksByReport,
        updateTask,
        deleteTask,
        parts,
        setParts,
        createPart,
        fetchPartsByReport,
        updatePart,
        deletePart,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
