const getFilterOptions = (filters) => ({
  reports: [
    {
      name: "vehicle",
      label: "Vehicle",
      value: filters.vehicle,
      placeholder: "Search for a vehicle",
    },
    {
      name: "created_at",
      label: "Date of Report",
      value: filters.created_at,
      type: "select",
    },
    {
      name: "owner",
      label: "Owner",
      value: filters.owner,
      type: "select",
    },
    {
      name: "user",
      label: "Created By",
      value: filters.user,
      type: "select",
    },
    {
      name: "status",
      label: "Status",
      value: filters.status,
      type: "select",
    },
  ],
  owners: [
    {
      name: "name",
      label: "Name",
      value: filters.name,
      placeholder: "Filter by name",
    },
    {
      name: "email",
      label: "Email",
      value: filters.email,
      type: "select",
    },
  ],
  vehicles: [
    {
      name: "brand",
      label: "Brand",
      value: filters.brand,
      type: "select",
    },
    {
      name: "model",
      label: "Model",
      value: filters.model,
      type: "select",
    },
    {
      name: "year",
      label: "Year",
      value: filters.year,
      type: "select",
    },
    {
      name: "license_plate",
      label: "License plate",
      value: filters.license_plate,
      placeholder: "Filter by License plate",
    },
    {
      name: "vehicle_owner",
      label: "Owner",
      value: filters.owner,
      type: "select",
    },
  ],
});

export default getFilterOptions;
