import React, { useContext, useState } from "react";
import { ReportContext } from "../contexts/ReportContext";
import { ClientContext } from "../contexts/ClientContext";
import FilterBar from "../components/FilterBar";
import Modal from "../components/Modal";
import "../styles/Reports.css";

const Reports = () => {
  const { reports } = useContext(ReportContext);
  const { clients } = useContext(ClientContext);
  const [filters, setFilters] = useState({
    title: "",
    created_date: "",
    client: "",
  });
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const filteredReports = reports.filter((report) => {
    return (
      (filters.title === "" ||
        report.title.toLowerCase().includes(filters.title.toLowerCase())) &&
      (filters.created_date === "" ||
        report.created_date.includes(filters.created_date)) &&
      (filters.client === "" || report.client.toString() === filters.client)
    );
  });

  const handleEditClick = (report) => {
    setSelectedReport(report);
    setShowEditModal(true);
  };
  const handleDeleteClick = (report) => {
    setSelectedReport(report);
    setShowDeleteModal(true);
  };
  const handleEditSubmit = (updatedReport) => {
    updateReport(selectedReport.id, updatedReport);
    setShowEditModal(false);
    setSelectedReport(null);
  };
  const handleDeleteConfirm = () => {
    deleteReport(selectedReport.id);
    setShowDeleteModal(false);
    setSelectedReport(null);
  };

  // Function to get the user name by ID
  const getClientNameById = (clientId) => {
    const client = clients.find((client) => client.id === clientId);
    return client ? client.name : "Unknown";
  };

  return (
    <div className="reports-section">
      {/* Filter Bar */}
      <FilterBar
        filterOptions={[
          {
            name: "title",
            label: "Title",
            value: filters.title,
            placeholder: "Filter by title",
          },
          {
            name: "created_date",
            label: "Created Date",
            value: filters.created_date,
            type: "select",
          },
          {
            name: "client",
            label: "Client",
            value: filters.client,
            type: "select",
          },
        ]}
        onFilterChange={handleFilterChange}
      />

      {/* Filtered Reports */}
      <div className="reports-list">
        {filteredReports.map((report) => (
          <div key={report.id} className="report-card">
            <h3>{report.title}</h3>
            <p>
              <strong>Created Date:</strong> {report.created_date}
            </p>
            <p>
              <strong>Client:</strong> {getClientNameById(report.client)}
            </p>
            <div className="report-actions">
              <button onClick={() => handleEditClick(report)}>Edit</button>
              <button onClick={() => handleDeleteClick(report)}>Delete</button>
            </div>
          </div>
        ))}
        {filteredReports.length === 0 && <p>No reports match your filters.</p>}
      </div>
      {/* Edit Modal */}
      {showEditModal && (
        <Modal onClose={() => setShowEditModal(false)}>
          <h2>Edit Report</h2>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const updatedReport = {
                title: e.target.title.value,
                created_date: e.target.created_date.value,
                client: e.target.client.value,
              };
              handleEditSubmit(updatedReport);
            }}
          >
            <label>
              Title:
              <input
                type="text"
                name="title"
                defaultValue={selectedReport.title}
              />
            </label>
            <label>
              Created Date:
              <input
                type="date"
                name="created_date"
                defaultValue={selectedReport.created_date}
              />
            </label>
            <label>
              Client:
              <select name="client" defaultValue={selectedReport.client}>
                {clients.map((client) => (
                  <option key={client.id} value={client.id}>
                    {client.name}
                  </option>
                ))}
              </select>
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setShowEditModal(false)}>
              Cancel
            </button>
          </form>
        </Modal>
      )}

      {/* Delete Modal */}
      {showDeleteModal && (
        <Modal onClose={() => setShowDeleteModal(false)}>
          <h2>Confirm Deletion</h2>
          <p>Are you sure you want to delete this report?</p>
          <button onClick={handleDeleteConfirm}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default Reports;
