import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import { Toast } from "../utils/sweetalert";

const InventoryContext = createContext();

export const InventoryProvider = ({ children }) => {
  const { authenticatedUser } = useAuth();
  const axiosInstance = useAxios();
  const [inventory, setInventory] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Fetch all items in Inventory
  const fetchInventory = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get("inventory/");
      setInventory(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  };

  // Update the inventory
  const updateInventory = async (inventoryId, updatedFields) => {
    // Debug
    console.log("Updating inventory data:", updatedFields);

    try {
      const response = await axiosInstance.patch(
        `inventory/${inventoryId}/`,
        updatedFields
      );
      setInventory((prevInventory) =>
        prevInventory.map((inventory) =>
          inventory.id === inventoryId ? response.data : inventory
        )
      );
      // Success notification
      Toast.fire({
        icon: "success",
        title: "Inventory updated successfully!",
      });
    } catch (err) {
      handleError(err);
    }
  };

  // Load inventory when user logs in or changes
  useEffect(() => {
    if (authenticatedUser && localStorage.getItem("token")) fetchInventory();
    else setInventory([]);
  }, [authenticatedUser]);

  return (
    <InventoryContext.Provider
      value={{
        inventory,
        updateInventory,
        loading,
        error,
      }}
    >
      {children}
    </InventoryContext.Provider>
  );
};

// Custom hook for accessing the InventoryContext
export const useInventoryContext = () => useContext(InventoryContext);
