export const validateUsername = (username, existingUsers) => {
  if (!username.trim()) return "Username is required.";
  if (username.length < 3)
    return "Username must be at least 3 characters long.";
  if (existingUsers.includes(username)) return "Username is already taken.";
  return "";
};

export const isTakenUsername = (username, existingUsers) => {
  if (existingUsers.includes(username)) return "Username is already taken.";
  return "";
};

export const validateEmail = (email, existingEmails) => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!email.trim()) return "Email is required.";
  if (!emailRegex.test(email)) return "Invalid email format.";
  if (existingEmails.includes(email)) return "Email is already registered.";
  return "";
};

export const validatePassword = (password) => {
  if (!password) return "Password is required.";
  if (password.length < 8)
    return "Password must be at least 8 characters long.";
  if (!/[A-Z]/.test(password))
    return "Password must contain at least one uppercase letter.";
  if (!/[a-z]/.test(password))
    return "Password must contain at least one lowercase letter.";
  if (!/[0-9]/.test(password))
    return "Password must contain at least one digit.";
  if (!/[@$!%*?&]/.test(password))
    return "Password must contain at least one special character (@$!%*?&).";
  return "";
};

export const validateConfirmPassword = (password, confirmPassword) => {
  if (!confirmPassword) return "Please confirm your password.";
  if (password !== confirmPassword) return "Passwords do not match.";
  return "";
};
