import React, { createContext, useContext, useState, useEffect } from "react";
import { useAxios } from "../utils/useAxios";
import { useAuth } from "./AuthContext";
import Swal from "sweetalert2";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Centralized error handling
  const handleError = (err) => {
    console.error(err.message || "An error occurred");
    setError(err.message || "An error occurred");
  };

  // Create a new report
  const createReport = async (reportData) => {
    try {
      if (!user) throw new Error("User is not authenticated");
      const response = await useAxios.post("/api/reports/", reportData);
      setReports((prevReports) => [...prevReports, response.data]);
    } catch (err) {
      handleError(err);
    }
  };

  // Update a report
  const updateReport = async (reportId, updatedFields) => {
    try {
      if (!user) throw new Error("User is not authenticated");
      const response = await useAxios.put(
        `/api/reports/${reportId}/`,
        updatedFields
      );
      setReports((prevReports) =>
        prevReports.map((report) =>
          report.id === reportId ? response.data : report
        )
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Delete a report
  const deleteReport = async (reportId) => {
    try {
      if (!user) throw new Error("User is not authenticated");
      await useAxios.delete(`/api/reports/${reportId}/`);
      setReports((prevReports) =>
        prevReports.filter((report) => report.id !== reportId)
      );
    } catch (err) {
      handleError(err);
    }
  };

  // Fetch reports when the provider mounts or user changes, only if the user is authenticated
  useEffect(() => {
    // Fetch reports
    const fetchReports = async () => {
      try {
        const response = await axiosInstance.get("reports/");
        setReports(response.data);
      } catch (error) {
        console.error("Failed to fetch reports:", error);
        setError("Failed to fetch reports. Please try again.");
        Swal.fire({
          icon: "error",
          title: "Error",
          text: "Failed to fetch reports. Please try again.",
        });
      }
    };

    if (user) {
      fetchReports();
    } else {
      setReports([]);
    }
  }, [user]);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
