import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useUserContext } from "../contexts/UserContext";
import {
  isValidUsername,
  isTakenUsername,
  isValidEmail,
  isTakenEmail,
  isValidPassword,
  passwordsMatch,
} from "../utils/validation";
import axios from "axios";
import Swal from "sweetalert2";
import "../styles/Auth.css";

const Register = () => {
  const { users } = useUserContext();
  const existingUsernames = users.map((user) => user.username);
  const existingEmails = users.map((user) => user.email);
  const navigate = useNavigate();

  // Form fields
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // Error messages
  const [errors, setErrors] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  // Live validation
  useEffect(() => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      username: isValidUsername(username, existingUsernames),
    }));
  }, [username]);

  useEffect(() => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      email: isValidEmail(email, existingEmails),
    }));
  }, [email]);

  useEffect(() => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      password: isValidPassword(password),
    }));
  }, [password]);

  useEffect(() => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      confirmPassword: isValidConfirmPassword(password, confirmPassword),
    }));
  }, [confirmPassword, password]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Send registration data to the backend
      await axios.post("http://localhost:8000/api/register/", {
        username,
        email,
        password,
      });

      // Show success message
      Swal.fire({
        icon: "success",
        title: "Registration Successful",
        text: "You have been registered successfully! Please log in.",
      });

      // Redirect to the login page
      navigate("/login");
    } catch (error) {
      console.error("Registration failed:", error);
      Swal.fire({
        icon: "error",
        title: "Registration Failed",
        text:
          error.response?.data?.error ||
          "An error occurred during registration. Please try again.",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Register</h2>
        <form onSubmit={handleSubmit} className="auth-form">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className={usernameError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{usernameError && <>{usernameError}</>}</p>

          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={emailError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{emailError && <>{emailError}</>}</p>

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={passwordError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">{passwordError && <>{passwordError}</>}</p>

          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className={confirmPasswordError ? "invalid" : "valid"}
            required
          />
          <p className="error-text">
            {confirmPasswordError && <>{confirmPasswordError}</>}
          </p>

          <button
            type="submit"
            disabled={
              !!usernameError ||
              !!emailError ||
              !!passwordError ||
              !!confirmPasswordError ||
              loading
            }
          >
            {loading ? "Registering..." : "Register"}
          </button>
        </form>
        <p className="bottomline">
          Already have an account? <Link to="/login">Log In</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
