// Contexts
import { useVehicleContext } from "../contexts/VehicleContext";

export const filterItems = (item, filters) => {
  const { vehicles } = useVehicleContext();

  if (!item) return false;

  // Handle different entity types
  if (filters.type === "report") {
    const vehicle = vehicles.find((v) => v.id === item.vehicle);
    const ownerId = vehicle ? vehicle.owner : null;

    return (
      (filters.vehicle === "" ||
        item.vehicle?.toString() === filters.vehicle) &&
      (filters.user === "" || item.user?.toString() === filters.user) &&
      (filters.created_at === "" ||
        item.formatted_created_at.includes(filters.created_at)) &&
      (filters.owner === "" || ownerId?.toString() === filters.owner) &&
      (filters.status === "" || item.status === filters.status)
    );
  }

  if (filters.type === "vehicle") {
    return (
      (filters.brand === "" || item.brand === filters.brand) &&
      (filters.model === "" || item.model === filters.model) &&
      (filters.year === "" || item.year?.toString() === filters.year) &&
      (filters.owner === "" || item.owner?.toString() === filters.owner) &&
      (filters.license_plate === "" ||
        item.license_plate.includes(filters.license_plate))
    );
  }

  return true;
};
