import React, { createContext, useContext, useState, useEffect } from "react";
// Hooks
import useCRUD from "../hooks/useCRUD";
// Utils
import { Toast } from "../utils/sweetalert";

const ReportContext = createContext();

export const ReportProvider = ({ children }) => {
  const [selectedReport, setSelectedReport] = useState(null);

  const {
    data: reports,
    createItem: createReport,
    updateItem: updateReport,
    deleteItem: deleteReport,
    loading,
    error,
  } = useCRUD("reports");

  const createReportAlert = async (reportData) => {
    const newReport = await createReport(reportData);
    if (newReport) {
      Toast.fire({
        icon: "success",
        title: "Report created successfully!",
      });
    }
    return newReport;
  };

  const {
    data: tasks,
    setData: setTasks,
    createItem: createTask,
    updateItem: updateTask,
    deleteItem: deleteTask,
  } = useCRUD("tasks", "reports", selectedReport?.id);

  const {
    data: parts,
    setData: setParts,
    createItem: createPart,
    updateItem: updatePart,
    deleteItem: deletePart,
  } = useCRUD("parts", "reports", selectedReport?.id);

  return (
    <ReportContext.Provider
      value={{
        reports,
        selectedReport,
        setSelectedReport,
        createReport,
        updateReport,
        deleteReport,
        tasks,
        setTasks,
        createTask,
        updateTask,
        deleteTask,
        parts,
        setParts,
        createPart,
        updatePart,
        deletePart,
        loading,
        error,
      }}
    >
      {children}
    </ReportContext.Provider>
  );
};

// Custom hook for accessing the ReportContext
export const useReportContext = () => useContext(ReportContext);
