import React, { useState } from "react";
// Components

// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useInventoryContext } from "../contexts/InventoryContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/ReportModal.css";

const CreateReportModal = ({ onClose }) => {
  const { authenticatedUser } = useAuth();
  const { createReport, createTask, createPart } = useReportContext();
  const { vehicles } = useVehicleContext();
  const { getOwnerNameByVehicleId } = useOwnerContext();
  const { inventory, tasks } = useInventoryContext();

  const [reportData, setReportData] = useState({
    vehicle_id: "",
    user_id: authenticatedUser.id,
    status: "pending",
    remarks: "",
  });

  const [repairTask, setRepairTask] = useState([]);
  const [selectedTask, setSelectedTask] = useState("");
  const [repairPart, setRepairPart] = useState([]);
  const [selectedPart, setSelectedPart] = useState("");

  const handleReportChange = (e) => {
    const { name, value } = e.target;

    setReportData({
      ...reportData,
      [name]: name === "vehicle" ? Number(value) : value,
    });
  };
  const handleTaskChange = (e) => {
    setSelectedTask(e.target.value);
  };
  const handlePartChange = (e) => {
    setSelectedPart(e.target.value);
  };

  const addTask = () => {
    if (!selectedTask) {
      Toast.fire("Error", "Please select a repair task.", "error");
      return;
    }

    const taskId = Number(selectedTask);
    const task = inventory.find((item) => item.id === taskId);
    if (!task) return;

    setRepairTask([
      ...repairTask,
      { task: task.id, description: task.name, total_cost: task.price },
    ]);

    setSelectedPart("");
  };

  const addPart = () => {
    if (!selectedPart) {
      Toast.fire("Error", "Please select a repair part.", "error");
      return;
    }

    const partId = Number(selectedPart);
    const part = inventory.find((item) => item.id === partId);
    if (!part) return;

    setRepairPart([
      ...repairPart,
      { part: part.id, description: part.name, total_cost: part.unit_price },
    ]);

    setSelectedPart("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!reportData.vehicle) {
      Toast.fire("Error", "Please select a vehicle.", "error");
      return;
    }

    try {
      const newReport = await createReport(reportData);
      if (newReport && repairTask.length > 0) {
        await Promise.all(
          repairTask.map((task) =>
            createTask({ ...task, report: newReport.id })
          )
        );
      }
      if (newReport && repairPart.length > 0) {
        await Promise.all(
          repairPart.map((part) =>
            createPart({ ...part, report: newReport.id })
          )
        );
      }

      Toast.fire("Success", "Report created successfully!", "success");
      onClose();
    } catch (error) {
      console.error("Error creating report:", error);
      Toast.fire("Error", "Something went wrong.", "error");
    }
  };

  return (
    <div className="modal-container">
      <div className="modal-card">
        <svg
          onClick={onClose}
          className="modal-card-close"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
        >
          <path
            width="100%"
            height="100%"
            d="M11.414 10l2.829-2.828a1 1 0 1 0-1.415-1.415L10 8.586 7.172 5.757a1 1 0 0 0-1.415 1.415L8.586 10l-2.829 2.828a1 1 0 0 0 1.415 1.415L10 11.414l2.828 2.829a1 1 0 0 0 1.415-1.415L11.414 10zM10 20C4.477 20 0 15.523 0 10S4.477 0 10 0s10 4.477 10 10-4.477 10-10 10z"
          />
        </svg>
        <h2>Create Report</h2>
        <form className="modal-form" onSubmit={handleSubmit}>
          <label>
            Vehicle:
            <select
              name="vehicle"
              value={reportData.vehicle}
              onChange={handleReportChange}
              required
            >
              <option value="">Select a vehicle</option>
              {vehicles.map((vehicle) => (
                <option key={vehicle.id} value={vehicle.id}>
                  {vehicle.__str__} - {getOwnerNameByVehicleId(vehicle.id)}
                </option>
              ))}
            </select>
          </label>

          <label>
            Status:
            <select
              name="status"
              value={reportData.status}
              onChange={handleReportChange}
              required
            >
              <option value="pending">Pending</option>
              <option value="in_progress">In Progress</option>
              <option value="completed">Completed</option>
            </select>
          </label>

          <label>
            Remarks:
            <textarea
              name="remarks"
              value={reportData.remarks}
              onChange={handleReportChange}
              placeholder="Enter remarks"
            />
          </label>

          <h3>Repairs Tasks</h3>
          <div className="repair-section">
            <select value={selectedTask} onChange={handleTaskChange}>
              <option value="">Select a repair task</option>
              {tasks.map((task) => (
                <option key={task.id} value={task.id}>
                  {task.name} - €{task.price}
                </option>
              ))}
            </select>
            <button type="button" onClick={addTask}>
              Add Task
            </button>
          </div>

          {repairTask.length > 0 && (
            <ul className="repair-list">
              {repairTask.map((task, index) => (
                <li key={index}>
                  {task.name} - €{task.price}
                </li>
              ))}
            </ul>
          )}

          <h3>Repairs Parts</h3>
          <div className="repair-section">
            <select value={selectedPart} onChange={handlePartChange}>
              <option value="">Select a repair part</option>
              {inventory.map((part) => (
                <option key={part.id} value={part.id}>
                  {part.name} - €{part.unit_price}
                </option>
              ))}
            </select>
            <button type="button" onClick={addPart}>
              Add Part
            </button>
          </div>

          {repairPart.length > 0 && (
            <ul className="repair-list">
              {repairPart.map((part, index) => (
                <li key={index}>
                  {part.description} - €{part.total_cost}
                </li>
              ))}
            </ul>
          )}

          <div className="button-group">
            <button type="submit">Create Report</button>
            <button type="button" className="cancel-btn" onClick={onClose}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateReportModal;
