import { useEffect, useState } from "react";

// Contexts
import { useAuth } from "../contexts/AuthContext";
import { useGlobalContext } from "../contexts/GlobalContext";
import { useReportContext } from "../contexts/ReportContext";
import { useVehicleContext } from "../contexts/VehicleContext";
import { useOwnerContext } from "../contexts/OwnerContext";
import { useInventoryContext } from "../contexts/InventoryContext";
// Utils
import { Toast } from "../utils/sweetalert";
// Styles
import "../styles/Modal.css";

const OwnerModal = ({ readonly, setReadonly, onClose, onDelete }) => {
  const { selectedItem, setSelectedItem } = useGlobalContext();

  const [ownerData, setOwnerData] = useState({
    full_name: selectedItem?.full_name || "",
    email: selectedItem?.email || "",
    phone: selectedItem?.phone || "",
    address: selectedItem?.address || "",
  });

  const handleOwnerChange = (e) => {
    const { name, value } = e.target;

    setOwnerData({
      ...ownerData,
      [name]: value,
    });
  };

  const toggleReadonly = (e) => {
    e.preventDefault();
    setReadonly(!readonly);
  };

  return (
    <div className="modal-container">
      <div className="modal-card">
        <svg
          onClick={onClose}
          className="modal-card-close"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
        >
          <path
            width="100%"
            height="100%"
            d="M11.414 10l2.829-2.828a1 1 0 1 0-1.415-1.415L10 8.586 7.172 5.757a1 1 0 0 0-1.415 1.415L8.586 10l-2.829 2.828a1 1 0 0 0 1.415 1.415L10 11.414l2.828 2.829a1 1 0 0 0 1.415-1.415L11.414 10zM10 20C4.477 20 0 15.523 0 10S4.477 0 10 0s10 4.477 10 10-4.477 10-10 10z"
          />
        </svg>
        {readonly ? (
          <h2>View Owner</h2>
        ) : selectedItem ? (
          <h2>Edit Owner</h2>
        ) : (
          <h2>Create Owner</h2>
        )}
        <form
          className="modal-form"
          onSubmit={selectedItem ? handleEditSubmit : handleCreateSubmit}
        >
          <label>
            Full Name:
            <input
              type="text"
              name="full_name"
              value={ownerData.full_name}
              onChange={handleOwnerChange}
              placeholder="Enter full name"
              required
              disabled={readonly}
            />
          </label>

          <label>
            Email:
            <input
              type="email"
              name="email"
              value={ownerData.email}
              onChange={handleOwnerChange}
              placeholder="Enter email"
              required
              disabled={readonly}
            />
          </label>

          <label>
            Address:
            <input
              type="text"
              name="address"
              value={ownerData.address}
              onChange={handleOwnerChange}
              placeholder="Enter address"
              disabled={readonly}
            />
          </label>

          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={ownerData.phone}
              onChange={handleOwnerChange}
              placeholder="Enter phone"
              disabled={readonly}
            />
          </label>

          <div className="button-group">
            {selectedItem ? (
              <>
                {readonly ? (
                  <button type="button" onClick={toggleReadonly}>
                    Edit Owner
                  </button>
                ) : (
                  <button type="submit" disabled={readonly}>
                    Update Owner
                  </button>
                )}
                <button type="button" onClick={onDelete}>
                  Delete
                </button>
              </>
            ) : (
              <button type="submit" disabled={readonly}>
                Create Owner
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};
export default OwnerModal;
