import React, { useContext, useState } from "react";
import { ReportContext } from "../contexts/ReportContext";

const Reports = () => {
  const {
    reports,
    selectedReport,
    setSelectedReport,
    updateReport,
    createReport,
  } = useContext(ReportContext);

  const [editableReport, setEditableReport] = useState(null); // Local editable state
  const [isEditable, setIsEditable] = useState(false); // Tracks if the form is editable

  const handleSelectChange = (e) => {
    const reportId = e.target.value;
    const report = reports.find((r) => r.id === parseInt(reportId));
    setSelectedReport(report);
    setEditableReport(report); // Reset local state
    setIsEditable(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditableReport({ ...editableReport, [name]: value });
  };

  const handleEditClick = () => {
    setIsEditable(true);
  };

  const handleSaveClick = () => {
    if (selectedReport && editableReport) {
      updateReport(editableReport); // Update the report in the context
      setIsEditable(false);
    }
  };

  const handleCreateClick = () => {
    setSelectedReport(null); // Deselect any report
    setEditableReport({
      title: "",
      description: "",
      date: "",
      // Add other fields here
    });
    setIsEditable(true); // Enable the form
  };

  return (
    <div className="reports-section">
      {/* Topbar: Select and Buttons */}
      <div className="topbar">
        <button onClick={handleCreateClick}>Create New Report</button>
        <select onChange={handleSelectChange} value={selectedReport?.id || ""}>
          <option value="" disabled>
            Select a Report
          </option>
          {reports.map((report) => (
            <option key={report.id} value={report.id}>
              {report.title}
            </option>
          ))}
        </select>
        <button
          onClick={handleEditClick}
          disabled={!selectedReport || isEditable}
        >
          Edit
        </button>
      </div>

      {/* Report Details/Form */}
      {editableReport && (
        <form className="report-form">
          <div className="form-group">
            <label>Title:</label>
            <input
              type="text"
              name="title"
              value={editableReport.title}
              onChange={handleInputChange}
              disabled={!isEditable}
            />
          </div>
          <div className="form-group">
            <label>Description:</label>
            <textarea
              name="description"
              value={editableReport.description}
              onChange={handleInputChange}
              disabled={!isEditable}
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              name="date"
              value={editableReport.date}
              onChange={handleInputChange}
              disabled={!isEditable}
            />
          </div>
          {/* Add other fields as needed */}

          {isEditable && (
            <button
              type="button"
              onClick={handleSaveClick}
              disabled={editableReport === selectedReport}
            >
              Save
            </button>
          )}
        </form>
      )}
    </div>
  );
};

export default Reports;
