import axios from "axios";
import { useAuth } from "../contexts/authContext";

export const useAxios = () => {
  const { user } = useAuth();

  const instance = axios.create({
    baseURL: "http://localhost:8000/api/",
    headers: {
      Authorization: user ? `Bearer ${localStorage.getItem("token")}` : "",
    },
  });

  return instance;
};
