const TaskTemplateCard = ({
  item,
  handleViewClick,
  handleEditClick,
  handleDeleteClick,
}) => {
  return (
    <div
      key={item.id}
      className="card"
      title="View task template"
      onClick={(e) => {
        // Prevent triggering view mode if clicking on an action button
        if (!e.target.closest(".actions")) {
          handleViewClick(item);
        }
      }}
    >
      <div>
        <h3>{item.name}</h3>
        <p>
          <strong>Description:</strong>&nbsp;{item.description}
        </p>
        <p>
          <strong>Price:</strong>&nbsp;{item.price}
        </p>
      </div>
      <div className="actions">
        <button className="btn btn-green" onClick={() => handleEditClick(item)}>
          Edit
        </button>
        <button className="btn btn-red" onClick={() => handleDeleteClick(item)}>
          Delete
        </button>
      </div>
    </div>
  );
};
export default TaskTemplateCard;
